import { ADD_NEWS, DELETE_NEWS, FETCH_NEWS_ERROR, FETCH_NEWS_PENDING, FETCH_NEWS_SUCCESS } from "./Reducers/Reducer";

export function addNews(newsdata) {
    console.log('addNews',newsdata);
    return {
        type: ADD_NEWS,
        payload: newsdata
    }   

}

