import React from 'react'
import { Form, Formik, useField } from 'formik'
import * as Yup from 'yup'
import { Link, useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addNews, getNews } from '../redux/Action';
import { useEffect } from 'react';
import { useState } from 'react';

const MyTextInput = ({ label, ...props }) => {
    const [field, meta] = useField(props);
    return (
        <>
            <label htmlFor={props.id || props.label}>{label}</label>
            <input className="text-input" {...field} {...props} />
            {meta.touched && meta.error ? (<div style={{ color: 'red' }} className="error">{meta.error}</div>) : null}
        </>
    )
}

const MySelect = ({ label, ...props }) => {
    const [field, meta] = useField(props);
    return (
        <div>
            <label htmlFor={props.id || props.name}>{label}</label>
            <select {...field} {...props} />
            {meta.touched && meta.error ? (<div style={{ color: 'red' }} className="error">{meta.error}</div>) : null}
        </div>

    );
}

const Edit = () => {
    const newsdata = useSelector(state => state.newsdata.newsdata)
    console.log("newsData ", newsdata);
    const { id } = useParams()
    // alert(id);
    console.log("newsdata ", newsdata);
    const dispatch = useDispatch();
    const [values, setValues] = useState(newsdata);
    console.log('values', values);
    const {title,description,created_date,publisher,category} = values

    useEffect(() => {
        if(newsdata != null){
            setValues({
                title,description,created_date,publisher,category
            })

        }
        dispatch(getNews(id))
    }, [newsdata])


    return (
        <>
            <h1>Edit News</h1>
            <Formik
                initialValues={{
                    title: '',
                    description: '',
                    created_date: '',
                    publisher: '',
                    category: ''

                }}
                validationSchema={Yup.object({
                    title: Yup.string()
                        .max(20, 'Must be 15 characters or less')
                        .required('Required'),
                    description: Yup.string()
                        .max(50, 'Must be 15 characters')
                        .required('Required'),
                    created_date: Yup.string()
                        .required('Required'),
                    publisher: Yup.string()
                        .required('Required'),
                    category: Yup.string()
                        .oneOf(
                            ["Software", "It", "Politics", "other"],
                            "Invalid category Type"
                        )
                        .required('Required'),


                })}
                onSubmit={(values) => {
                    // dispatch(addNews(values))
                }}
            >
                <Form
                    className="col-lg-1 col-offset-6 centered">
                    <MyTextInput
                        label="Title"
                        name="title"
                        type="text"
                    /><br />

                    <MyTextInput
                        label="Description"
                        name="description"
                        type="text"
                    /><br />

                    <MyTextInput
                        label="Created_date"
                        name="created_date"
                        type="date"
                    /><br />

                    <MyTextInput
                        label="Publisher"
                        name="publisher"
                        type="text"
                    /><br />

                    <MySelect label='Category' name='category'>
                        <option value="">Select a News type</option>
                        <option value="Software">Software</option>
                        <option value="It">It</option>
                        <option value="Politics">Politics</option>
                        <option value="other">Other</option>
                    </MySelect><br />

                    <button type="submit">Update</button>
                    <button type="reset">Reset</button>,
                    <Link to='/' className="btn btn-primary"><button>Cancel</button></Link><br /><br />
                </Form>
            </Formik>
            {/* <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Title</th>
                        <th scope="col">Description</th>
                        <th scope="col">Created_date</th>
                        <th scope="col">Publisher</th>
                        <th scope="col">Category</th>

                    </tr>
                </thead>
                {newsdata.length > 0 && newsdata.map((data) => {
                    return (


                        <tr>
                            <td>{data.title}</td>
                            <td>{data.description}</td>
                            <td>{data.created_date}</td>
                            <td>{data.publisher}</td>
                            <td>{data.category}</td>
                            <button>Edit</button>
                            <button>Delete</button>


                        </tr>
                    )

                })}
                <tbody>

                </tbody>
            </table> */}
        </>
    )
}

export default Edit
