import React, { useEffect, useState } from 'react'
import ReactPaginate from 'react-paginate';
import JSONDATA from '../Data/data.json'


const Pagination = () => {

    const PER_PAGE = 10;

    const [currentPage, setCurrentPage] = useState(0);
    const [data, setData] = useState([]);

    const offset = currentPage * PER_PAGE;

    const currentPageData = data.slice(offset, offset + PER_PAGE).map((ele) => {
        return (

            <tr key={ele.id}>
                <td>{ele.title}</td>
                <td>{ele.author}</td>
                <td>{ele.source?.name}</td>
                <td>{ele.name}</td>
                <td>{publish}</td>

                <Link to={{
                    pathname: `edit/${id}`,
                    news: {
                        nid: id,
                        title: data.title,
                        author: data.author,
                        name: data.source?.name,

                    }

                }} ><button>Edit</button></Link>
                <button onClick={() => dispatch(deleteNews(id))} >Delete</button>
            </tr>
        )
    })

    const pageCount = Math.ceil(data.length / PER_PAGE);

    useEffect(() => {
        fetchData();
    }, []);
    function fetchData() {
        fetch('https://newsapi.org/v2/everything?q=apple&from=2021-04-28&to=2021-04-28&sortBy=popularity&apiKey=e312b920029441008b7f30a84c125522')
            .then((res) => res.json())
            .then((data) => {
                const { articles } = data
                setData(articles)
            });
    }


    function handlePageClick({ selected: selectedPage }) {
        setCurrentPage(selectedPage);
    }
    return (
        <div>
            Pagination
            <ReactPaginate
                previousLabel={"← Previous"}
                nextLabel={"Next →"}
                pageCount={pageCount}
                onPageChange={handlePageClick}
                containerClassName={"pagination"}
                previousLinkClassName={"pagination__link"}
                nextLinkClassName={"pagination__link"}
                disabledClassName={"pagination__link--disabled"}
                activeClassName={"pagination__link--active"}
            />
            {currentPageData}
        </div>
    )
}

export default Pagination
