const Edit = (props) => {
    const newsinfo = useSelector(state => state.news.news)
    console.log("news  in add component", newsinfo);
    const { id } = useParams();

    const {title, author,} = newsinfo
    console.log('id from params', id);
    const dispatch = useDispatch();
    const history = useHistory();


    // console.log('props value',props);
    // console.log('fields', field);

    useEffect(() => {
        dispatch(getNews(id))
    },[])

    return (
        <>
            <h1>Edit News</h1>
            <Formik
                initialValues={{
                    title: '',
                    author: '',
                    name: '',
                    publishedAt: '',
                    // category: ''

                }}
                validationSchema={Yup.object({
                    title: Yup.string()
                        .max(20, 'Must be 15 characters or less')
                        .required('Required'),
                    author: Yup.string()
                        .max(50, 'Must be 15 characters')
                        .required('Required'),
                    name: Yup.string()
                        .required('Required'),
                    publishedAt: Yup.string()
                        .required('Required'),
                    // category: Yup.string()
                    //     .oneOf(
                    //         ["Software", "It", "Politics", "other"],
                    //         "Invalid category Type"
                    //     )
                    //     .required('Required'),


                })}
                onSubmit={(values) => {
                    // alert(JSON.stringify(values))
                    dispatch(addNews(values))
                    history.push('/')
                }}
            >
                {props => (

                    <Form onSubmit={props.handleSubmit}
                        className="col-lg-1 col-offset-6 centered">
                        <MyTextInput
                            label="Title"
                            name="title"
                            type="text"
                            onChange={props.handleChange}
                            value={props.values.title}

                        /><br />

                        <MyTextInput
                            label="Author"
                            name="author"
                            type="text"
                            onChange={props.handleChange}
                            value={props.values.author}
                        /><br />

                        <MyTextInput
                            label="Name"
                            name="name"
                            type="text"
                            onChange={props.handleChange}
                            value={props.values.name}
                        /><br />

                        <MyTextInput
                            label="PublishedAt"
                            name="publishedAt"
                            type="date"
                            onChange={props.handleChange}
                            value={props.values.publishedAt}
                        /><br /><br /><br />

                        {/* <MySelect label='Category' name='category'>
                            <option value="">Select a News type</option>
                            <option value="Software">Software</option>
                            <option value="It">It</option>
                            <option value="Politics">Politics</option>
                            <option value="other">Other</option>
                        </MySelect><br /> */}

                        <button type="submit">Update</button>
                        <button type="reset">Reset</button>,
                        <Link to='/' className="btn btn-primary"><button>Cancel</button></Link><br /><br />
                    </Form>
                )}
            </Formik>
            <table className="table">
                {/* <thead>
                    <tr>
                        <th scope="col">Title</th>
                        <th scope="col">Description</th>
                        <th scope="col">Created_date</th>
                        <th scope="col">Publisher</th>
                        <th scope="col">Category</th>

                    </tr>
                </thead> */}
                {/* {newsdata.map((data,id) => {
                    console.log(id)
                    return (


                        <tr key={id}>
                            <td>{data.title}</td>
                            <td>{data.description}</td>
                            <td>{data.created_date}</td>
                            <td>{data.publisher}</td>
                            <td>{data.category}</td>
                            <Link to = '/edit'></Link><button>Edit</button>
                            <button>Delete</button>


                        </tr>
                    )

                })} */}
                <tbody>

                </tbody>
            </table>
        </>
    )
}

export default Edit
