import React,  { useEffect } from 'react'
// import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
// import { deleteNews } from '../redux/Action'
import { connect } from 'react-redux';
import { fetchNewsData } from '../redux/Action';

const Home = () => ({ news, fetchNewsData }) => {
    console.log('news in container', news);

    // useEffect(() => {
    //     fetchNewsData()
    // }, [])


    return (
        news.loading ? (
            <h2>Loading</h2>
        ) : news.error ? (
            <h2>{news.error}</h2>
        ) : (
            <div>
                HOME PAGE
                <Link to='/add'><button>Add</button></Link>
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">Title</th>
                            <th scope="col">Description</th>
                            <th scope="col">Created_date</th>
                            <th scope="col">Publisher</th>
                            <th scope="col">Category</th>

                        </tr>
                    </thead>
                    {news.map((data, id) => {
                        console.log(id)
                        return (
                            <tr key={id}>
                                <td>{data.title}</td>
                                <td>{data.description}</td>
                                <td>{data.created_date}</td>
                                <td>{data.publisher}</td>
                                <td>{data.category}</td>
                                <Link to={`edit/${id}`}><button>Edit</button></Link>
                                {/* <button onClick={() => dispatch(deleteNews(id))} >Delete</button> */}
                            </tr>
                        )

                    })}
                </table>
            </div>

        )
    )
}

const mapStateToProps = state => {
    console.log('state toprops', state, 'news', state.news.news);
    return {
        news: state.news.news

    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchNewsData: () => dispatch(fetchNewsData())
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Home)

