import React, { useEffect } from 'react'
// import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { deleteNews } from '../redux/Action'
import { connect, useDispatch, useSelector } from 'react-redux';
import { fetchNewsData } from '../redux/Action';

const Home = ({ news, fetchNewsData }) => {
    // console.log('news in container', news);

    const dispatch = useDispatch();

    useEffect(() => {
        // fetchNewsData()
    }, [])



    return (
        news.loading ? (
            <h2>Loading</h2>
        ) : news.error ? (
            <h2>{news.error}</h2>
        ) : (
            <div>
                HOME PAGE
                <Link to='/add'><button>Add</button></Link>
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">Title</th>
                            <th scope="col">Author</th>
                            <th scope="col">Name</th>
                            <th scope="col">PublishedAt</th>
                            <th scope="col">Phone Numbers</th>

                            {/* <th scope="col"></th> */}

                        </tr>
                    </thead>
                    {news.map((data = {}, id) => {
                        {/* console.log(data) */ }
                        return (
                            <tr key={id}>
                                <td>{data.title}</td>
                                <td>{data.author}</td>
                                <td>{data.name}</td>
                                <td>{data.publishedAt}</td>
                                <td>{data.phnumbers.map((ele) =><td>{ele}</td>)}</td>

                                {/* api rsponse 
                                <td>{data.title}</td>
                                <td>{data.author}</td>
                                <td>{data.source?.name}</td>
                                <td>{data.publishedAt}</td>
                                <td>{data.phnumbers.map((ele) =><td>{ele}</td>)}</td> */}


                                {/* <td>{data.category}</td> */}
                                <Link to={{
                                    pathname: `edit/${id}`,
                                    news: {
                                        title: data.title,
                                        author: data.author,
                                        name: data.name,
                                        publishedAt: data.publishedAt,
                                        phnumbers:data.phnumbers
                                    }

                                    //api response
                                    // news: {
                                    //     title: data.title,
                                    //     author: data.author,
                                    //     name: data.source?.name,
                                    //     publishedAt: data.publishedAt,
                                    //     phnumbers:data.phnumbers
                                    // }


                                }} ><button>Edit</button></Link>
                                <button onClick={() => dispatch(deleteNews(id))} >Delete</button>
                            </tr>
                        )

                    })}
                </table>
            </div>

        )
    )
}

const mapStateToProps = state => {
    // console.log('state toprops', state, 'news', state.news.news);
    return {
        news: state.news.news

    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchNewsData: () => dispatch(fetchNewsData())
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Home)

