import React, { useEffect } from 'react'
// import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { deleteNews } from '../Actions/Action'
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'
import JSONDATA from '../Data/data.json'


const Search = () => {
    // console.log('news in container', news);
    return  (
            <div>
                <h2>Search PAGE</h2>
                <Link to='/add'><button>Add</button></Link><br /><br />

                <div>
                    <input type = "text" placeholder="Search..."></input>
                </div>

                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">Title</th>
                            <th scope="col">Author</th>
                            <th scope="col">Name</th>
                            <th scope="col">PublishedAt</th>
                            <th scope="col">Phone Numbers</th>
                            {/* <th scope="col"></th> */}
                        </tr>
                    </thead>

                    {JSONDATA.map((data = {}, id) => {
                        {/* console.log(data) */ }
                        return (
                            <tr key={data.id}>
                                <td>{data.title}</td>
                                <td>{data.author}</td>
                                <td>{data.source?.name}</td>
                                {/* <td>{data.name}</td> */}
                                <td>{data.publishedAt}</td>
                                {/* <td>{data.phnumbers.map((ele) => <td>{ele}</td>)}</td> */}

                                {/* api name rsponse 
                                <td>{data.source?.name}</td>
                                {/* <td>{data.category}</td> */}
                                <Link to={{
                                    pathname: `edit/${id}`,
                                    news: {
                                        nid: id,
                                        title: data.title,
                                        author: data.author,
                                        name: data.source?.name,
                                        // name: data.name,
                                        publishedAt: data.publishedAt,
                                        // phnumber: data.phnumbers
                                    }
                                    //api name response
                                    //name: data.source?.name,
                                }} ><button>Edit</button></Link>
                                <button onClick={() => dispatch(deleteNews(id))} >Delete</button>
                            </tr>
                        )
                    })}
                </table>
            </div>
        )

}



export default Search
