                            <th scope="col">Author</th>
                            <th scope="col">Name</th>
                            <th scope="col">PublishedAt</th>
                            {/* <th scope="col"></th> */}

                        </tr>
                    </thead>
                    {news.map((data = {}, id) => {
                        {/* console.log(data) */}
                        return (
                            <tr key={id}>
                                <td>{data.title}</td>
                                <td>{data.author}</td>
                                <td>{data.name}</td>
                                <td>{data.publishedAt}</td>
                                {/* <td>{data.category}</td> */}
                                <Link to={{pathname: `edit/${id}`,
                                            news:{
                                                title: data.title,
                                                author:data.author,
                                                name:data.name,
                                                publishedAt:data.publishedAt
                                            }

                                }} ><button onClick={getData}>Edit</button></Link>
                                <button onClick={() => dispatch(deleteNews(id))} >Delete</button>
                            </tr>
                        )

                    })}
                </table>
            </div>

        )
    )
}

const mapStateToProps = state => {
    // console.log('state toprops', state, 'news', state.news.news);
    return {
        news: state.news.news

    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchNewsData: () => dispatch(fetchNewsData())
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Home)

