import React, { useEffect } from 'react'
// import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { deleteNews } from '../Actions/Action'
import { connect, useDispatch } from 'react-redux';
import { fetchNewsData } from '../Actions/Action';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'


const Home = ({ news, fetchNewsData }) => {
    // console.log('news in container', news);
    const dispatch = useDispatch();

    useEffect(() => {
        fetchNewsData()
    }, [])

    return (
        news.loading ? (
            <h2>Loading</h2>
        ) : news.error ? (
            <h2>{news.error}</h2>
        ) : (
            <div>
                <h2>HOME PAGE</h2>
                <Link to='/add'><button>Add</button></Link><br /><br />
                <div>
                    <DatePicker />
                    <button>Filter</button>
                </div>

                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">Title</th>
                            <th scope="col">Author</th>
                            <th scope="col">Name</th>
                            <th scope="col">PublishedAt</th>
                            <th scope="col">Phone Numbers</th>
                            {/* <th scope="col"></th> */}
                        </tr>
                    </thead>

                    {news.map((data = {}, id) => {
                        {/* console.log(data) */ }
                        return (
                            <tr key={data.id}>
                                <td>{data.title}</td>
                                <td>{data.author}</td>
                                <td>{data.source?.name}</td>
                                {/* <td>{data.name}</td> */}
                                <td>{data.publishedAt}</td>
                                {/* <td>{data.phnumbers.map((ele) => <td>{ele}</td>)}</td> */}

                                {/* api name rsponse 
                                <td>{data.source?.name}</td>
                                {/* <td>{data.category}</td> */}
                                <Link to={{
                                    pathname: `edit/${id}`,
                                    news: {
                                        nid: id,
                                        title: data.title,
                                        author: data.author,
                                        name: data.source?.name,
                                        // name: data.name,
                                        publishedAt: data.publishedAt,
                                        // phnumber: data.phnumbers
                                    }
                                    //api name response
                                    //name: data.source?.name,
                                }} ><button>Edit</button></Link>
                                <button onClick={() => dispatch(deleteNews(id))} >Delete</button>
                            </tr>
                        )
                    })}
                </table>
            </div>
        )
    )
}

const mapStateToProps = state => {
    // console.log('state toprops', state, 'news', state.news.news);
    return {
        news: state.news.news

    }
}

const mapDispatchToProps = dispatch => {
    return {
        fetchNewsData: () => dispatch(fetchNewsData())
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Home)

