import React, { useState } from 'react'
// import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { deleteNews } from '../Actions/Action'
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'
import JSONDATA from '../Data/data.json'
import moment from 'moment';


const Search = () => {
    const [searchtitle, setsearchTitle] = useState("");
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [isFilter, setIsFilter] = useState(false);

    // console.log('start date' ,startDate);

    const renderFilterData = () => {
        let filteredData = JSONDATA.filter((val) => {

            if (searchtitle == "") {
                return val
            } else if (val.title.toLowerCase().includes(searchtitle.toLowerCase())) {
                return val
            }
        })
        if (isFilter) {
            filteredData = filteredData.filter((val) => {
                console.log('val', JSONDATA);
                const sdate = moment(startDate)
                const edate = moment(endDate)
                const publishedAt = moment(val.publishedAt)

                // var s = "2021-04-28T11:00:00Z"
                // var e = "2021-04-30T11:00:00Z"

                // console.log('value of sdate', sdate);
                // console.log('value of sdate', edate);
                // console.log('value of pubblished', publishedAt);
                return publishedAt.isSameOrAfter(sdate) && publishedAt.isSameOrBefore(edate)
            })
        }
        filterData.map((data = {}, id) => {
            {/* console.log(data) */ }
            return (
                <tr key={data.id}>
                    <td>{data.title}</td>
                    <td>{data.author}</td>
                    <td>{data.source?.name}</td>
                    {/* <td>{data.name}</td> */}
                    <td>{data.publishedAt}</td>
                    {/* <td>{data.phnumbers.map((ele) => <td>{ele}</td>)}</td> */}

                    {/* api name rsponse 
                        <td>{data.source?.name}</td>
                        {/* <td>{data.category}</td> */}
                    <Link to={{
                        pathname: `edit/${id}`,
                        news: {
                            nid: id,
                            title: data.title,
                            author: data.author,
                            name: data.source?.name,
                            // name: data.name,
                            publishedAt: data.publishedAt,
                            // phnumber: data.phnumbers
                        }
                        //api name response
                        //name: data.source?.name,
                    }} ><button>Edit</button></Link>
                    <button onClick={() => dispatch(deleteNews(id))} >Delete</button>
                </tr>
            )

        })
        return filteredData;
    }


    const filterData = () => {
        const result = JSONDATA.filter((val) => {
            console.log('val', JSONDATA);
            const sdate = moment(startDate)
            const edate = moment(endDate)
            const publishedAt = moment(val.publishedAt)

            // var s = "2021-04-28T11:00:00Z"
            // var e = "2021-04-30T11:00:00Z"

            // console.log('value of sdate', sdate);
            // console.log('value of sdate', edate);
            // console.log('value of pubblished', publishedAt);
            return publishedAt.isSameOrAfter(sdate) && publishedAt.isSameOrBefore(edate)
        }).map((ele) => {
            console.log('ele', ele);
        })
        console.log('result', result);
    }

    return (
        <div>
            <div>
                <div>
                    <DatePicker selected={startDate} onChange={date => setStartDate(date)} />
                    <DatePicker selected={endDate} onChange={date => setEndDate(date)} />
                    <button onClick={filterData}>Filter</button>
                </div><br />
                <input type="text"
                    placeholder="Search..."
                    onChange={(e) => {
                        setsearchTitle(e.target.value)
                    }}

                ></input>
            </div>

            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Title</th>
                        <th scope="col">Author</th>
                        <th scope="col">Name</th>
                        <th scope="col">PublishedAt</th>
                        <th scope="col">Phone Numbers</th>
                        {/* <th scope="col"></th> */}
                    </tr>
                </thead>
                {renderFilterData()}

            </table>
        </div>
    )

}



export default Search
