import { ADD_NEWS, DELETE_NEWS, GET_NEWS } from "./Reducers/Reducer";

export function addNews(newsdata) {
    console.log('addNews',newsdata);
    return {
        type: ADD_NEWS,
        payload: newsdata
    }   

}

export function getNews(newsid) {
    console.log('addNews',newsid);
    return {
        type: GET_NEWS,
        payload: newsid
    }   

}

export const deleteNews = (id) => ({
    type: DELETE_NEWS,
    payload:id
})
