export const ADD_NEWS = 'ADD_NEWS'
export const GET_NEWS = 'GET_NEWS'
export const DELETE_NEWS = 'DELETE_NEWS'

const initialstate = {
    newsdata: [],
    news:null
}

const Reducer = (state = initialstate, action) => {
    switch (action.type) {
        case ADD_NEWS:
            console.log('action', action.payload);

            return {
                newsdata: [action.payload, ...state.newsdata],

            }

        case GET_NEWS:
            let arr  = state.newsdata.filter(news => news.id === action.payload)
            arr = arr.values();
            for(let val of arr){
                arr = val
            }
            return{
                ...state,
                news: arr

            }

            case DELETE_NEWS:
                return{
                    ...state,
                    newsdata: state.newsdata.filter(news => news.id !== action.payload)
                }


        default:
            return state
    }
}

export default Reducer
