import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { deleteNews, getNews } from '../redux/Action'

const Home = () => {

    // const [news, setNews] = useState([]);

    // const getNews = async () => {
    //     const response = await fetch('https://newsapi.org/v2/everything?q=tesla&from=2021-03-25&sortBy=publishedAt&apiKey=e312b920029441008b7f30a84c125522')
    //     console.log('res', response);
    //     const data = await response.json();
    //     console.log(data);
    // }



    useEffect(() => {
        getNews();
    }, [])

    const newsdata = useSelector(state => state.newsdata.newsdata)
    const dispatch = useDispatch()
    return (
        <div>
            HOME PAGE
            <Link to='/add'><button>Add</button></Link>
            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Title</th>
                        <th scope="col">Description</th>
                        <th scope="col">Created_date</th>
                        <th scope="col">Publisher</th>
                        <th scope="col">Category</th>

                    </tr>
                </thead>
                {newsdata.map((data, id) => {
                    console.log(id)
                    return (
                        <tr key={id}>
                            <td>{data.title}</td>
                            <td>{data.description}</td>
                            <td>{data.created_date}</td>
                            <td>{data.publisher}</td>
                            <td>{data.category}</td>
                            <Link to={`edit/${id}`}><button>Edit</button></Link>
                            <button onClick ={() => dispatch(deleteNews(id))} >Delete</button>
                        </tr>
                    )

                })}
            </table>
        </div>

    )
}

export default Home
