import { ADD_NEWS, DELETE_NEWS, FETCH_NEWS_ERROR, FETCH_NEWS_PENDING, FETCH_NEWS_SUCCESS, GET_NEWS } from "./Reducers/Reducer";

export function addNews(newsdata) {
    console.log('addNews',newsdata);
    return {
        type: ADD_NEWS,
        payload: newsdata
    }   

}

export function getNews(newsid) {
    console.log('addNews',newsid);
    return {
        type: GET_NEWS,
        payload: newsid
    }   

}

export const deleteNews = (id) => {
    console.log('delete', id);
    return{
    type: DELETE_NEWS,
    payload:id
}}


export function fetchNewsPending() {
    return {
        type: FETCH_NEWS_PENDING
    }
}

export function fetchNewsSuccess(news) {
    return {
        type: FETCH_NEWS_SUCCESS,
        NEWS: news
    }
}

export function fetchNewsError(error) {
    return {
        type: FETCH_NEWS_ERROR,
        error: error
    }
}
