export const ADD_NEWS = 'ADD_NEWS'

const initialstate = {
    newsdata: []
}

const Reducer = (state = initialstate, action) => {
    switch (action.type) {
        case ADD_NEWS:
            console.log('action', action.payload);

            return {
                newsdata: [action.payload, ...state.newsdata],

            }

        default:
            return state
    }
}

export default Reducer
