import React, { useEffect, useState } from 'react'
// import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { deleteNews } from '../Actions/Action'
import { connect, useDispatch } from 'react-redux';
// import { fetchNewsData } from '../Actions/Action';
// import Search from './Search';
import JSONDATA from '../Data/data.json'
import ReactPaginate from 'react-paginate';



const Home = ({ news }) => {
    // console.log('news in container', news);
    const PER_PAGE = 5;
    const dispatch = useDispatch();

    const [data, setData] = useState(JSONDATA);
    const [currentPage, setCurrentPage] = useState(0);

    const offset = currentPage * PER_PAGE;
    const pageCount = Math.ceil(data.length / PER_PAGE);

    function handlePageClick({ selected: selectedPage }) {
        setCurrentPage(selectedPage);
    }

    useEffect(() => {
        // fetchNewsData()
        setData(JSONDATA)
    }, [])

    return (
        news.loading ? (
            <h2>Loading</h2>
        ) : news.error ? (
            <h2>{news.error}</h2>
        ) : (
            <div>
                <h2>HOME PAGE</h2>
                <Link to='/add'><button>Add</button></Link><br /><br />

                {/* <Search /> */}
                <table className="table">
                    <thead>
                        <tr>
                            {/* <th scope="col">Title</th>
                            <th scope="col">Author</th>
                            <th scope="col">Name</th>
                            <th scope="col">PublishedAt</th>
                            <th scope="col">Phone Numbers</th> */}
                            {/* <th scope="col"></th> */}
                        </tr>
                    </thead>

                    {JSONDATA.slice(offset, offset + PER_PAGE).map((data = {}, id) => {
                        console.log(JSONDATA)
                        return (
                            <tr key={data.id}>
                                <td>{data.title}</td>
                                <td>{data.author}</td>
                                <td>{data.source?.name}</td>
                                {/* <td>{data.name}</td> */}
                                <td>{data.publishedAt}</td>
                                {/* <td>{data.phnumbers.map((ele) => <td>{ele}</td>)}</td> */}

                                {/* api name rsponse 
                                <td>{data.source?.name}</td>
                                {/* <td>{data.category}</td> */}
                                <Link to={{
                                    pathname: `edit/${id}`,
                                    news: {
                                        nid: id,
                                        title: data.title,
                                        author: data.author,
                                        name: data.source?.name,
                                        // name: data.name,
                                        publishedAt: data.publishedAt,
                                        // phnumber: data.phnumbers
                                    }
                                    //api name response
                                    //name: data.source?.name,
                                }} ><button>Edit</button></Link>
                                <button onClick={() => dispatch(deleteNews(id))} >Delete</button>
                            </tr>
                        )
                    })}
                </table>
                <ReactPaginate
                    pageCount={pageCount}
                    onPageChange={handlePageClick}
                />
            </div>
        )
    )
}

const mapStateToProps = state => {
    // console.log('state toprops', state, 'news', state.news.news);
    return {
        news: state.news.news

    }
}

const mapDispatchToProps = dispatch => {
    return {
        // fetchNewsData: () => dispatch(fetchNewsData())
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Home)

