import { ADD_NEWS, DELETE_NEWS, FETCH_NEWS_ERROR, FETCH_NEWS_PENDING, FETCH_NEWS_SUCCESS, GET_NEWS } from "../../contants/constant"



const initialstate = {
    loading: false,
    error: '',
    // newsdata: [],
    news: [],
    newsinfo:null
}

const Reducer = (state = initialstate, action) => {
    switch (action.type) {
        case ADD_NEWS:
            // console.log('action', action.payload);

            return {
                news: [action.payload, ...state.news],

            }


        case GET_NEWS:
            console.log(state.news);
            let arr  = state.news.filter(data => {
                console.log('data', data);
            })
            arr = arr.values();
            for(let val of arr){
                arr = val
            }
            return{
                ...state,
                newsinfo: arr

            }

        case DELETE_NEWS:
            // console.log('action', action.payload);

            return {
                ...state,
                news: state.news.filter(news =>
                    news.id !== action.payload ,
                    // console.log('news', news.id)
                )
            }

        case FETCH_NEWS_PENDING:
            // console.log('in pending case' , action.payload);

            return {
                ...state,
                loading: true
            }
        case FETCH_NEWS_SUCCESS:
            // console.log('in success case' ,'hi', state, 'action',action);
            return {
                ...state,
                loading: false,
                news: action.news
            }
        case FETCH_NEWS_ERROR:
            // console.log('in error case' , action.payload);

            return {
                ...state,
                loading: false,
                error: action.error
            }

        default:
            return state
    }
}

export default Reducer
export const getNews = state => state.news;
export const getNewsPending = state => state.loading;
export const getNewsError = state => state.error;
