import React, { useState } from 'react'
// import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { deleteNews } from '../Actions/Action'
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'
import JSONDATA from '../Data/data.json'
import moment from 'moment';



const Search = () => {
    const [searchtitle, setsearchTitle] = useState("");
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());

    // console.log('start date' ,startDate);

    const filterData = () => {
      const result = JSONDATA.filter((val) => {
        //   console.log('val' , val);
            const sdate = moment(startDate).format('YYYY-MM-DD')
            const edate = moment(endDate).format('YYYY-MM-DD')
            const publishedAt = moment(val.publishedAt).format('YYYY-MM-DD')

            // console.log('value of sdate', sdate);
            // console.log('value of sdate', edate);
            // console.log('value of pubblished', publishedAt);


            return val.publishedAt <= edate
        })
        console.log('result' , result);
    }

    return (
        <div>
            <div>
                <div>
                    <DatePicker selected={startDate} onChange={date => setStartDate(date)} />
                    <DatePicker selected={endDate} onChange={date => setEndDate(date)} />
                    <button onClick={filterData}>Filter</button>
                </div><br />
                <input type="text"
                    placeholder="Search..."
                    onChange={(e) => {
                        setsearchTitle(e.target.value)
                    }}

                ></input>
            </div>

            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Title</th>
                        <th scope="col">Author</th>
                        <th scope="col">Name</th>
                        <th scope="col">PublishedAt</th>
                        <th scope="col">Phone Numbers</th>
                        {/* <th scope="col"></th> */}
                    </tr>
                </thead>

                {JSONDATA.filter((val) => {

                    if (searchtitle == "") {
                        return val
                    } else if (val.title.toLowerCase().includes(searchtitle.toLowerCase())) {
                        return val
                    }
                }).map((data = {}, id) => {
                    {/* console.log(data) */ }
                    return (
                        <tr key={data.id}>
                            <td>{data.title}</td>
                            <td>{data.author}</td>
                            <td>{data.source?.name}</td>
                            {/* <td>{data.name}</td> */}
                            <td>{data.publishedAt}</td>
                            {/* <td>{data.phnumbers.map((ele) => <td>{ele}</td>)}</td> */}

                            {/* api name rsponse 
                                <td>{data.source?.name}</td>
                                {/* <td>{data.category}</td> */}
                            <Link to={{
                                pathname: `edit/${id}`,
                                news: {
                                    nid: id,
                                    title: data.title,
                                    author: data.author,
                                    name: data.source?.name,
                                    // name: data.name,
                                    publishedAt: data.publishedAt,
                                    // phnumber: data.phnumbers
                                }
                                //api name response
                                //name: data.source?.name,
                            }} ><button>Edit</button></Link>
                            <button onClick={() => dispatch(deleteNews(id))} >Delete</button>
                        </tr>
                    )
                })}
            </table>
        </div>
    )

}



export default Search
